mapper.update:
UPDATE backstage_users
SET password = #{password,jdbcType=VARCHAR},
salt = #{salt,jdbcType=VARCHAR},
 gmt_update = #{gmtUpdate,jdbcType=BIGINT},
modifier = #{modifier,jdbcType=VARCHAR}
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT backstage_users.id as id, backstage_users.name as name, backstage_users.founder as founder, backstage_users.modifier as modifier, backstage_users.gmt_create as gmtCreate, backstage_users.gmt_update as gmtUpdate, roles.role_name as roleName FROM backstage_users INNER JOIN roles ON backstage_users.role_id=roles.id
name LIKE '%${name}%'
AND role_id=#{roleId}
