mapper.select:
select

from claim_info


        claim_code = #{claimCode,jdbcType=VARCHAR}


        and creditor_name = #{creditorName,jdbcType=VARCHAR}


        and creditor_phone = #{creditorPhone,jdbcType=VARCHAR}


        and creditor_id_number = #{creditorIdNumber,jdbcType=VARCHAR}


        and status = #{status,jdbcType=INTEGER}


        and lending_date   >=  #{lowerLendingDate,jdbcType=BIGINT}


        and lending_date   <=  #{upperLendingDate,jdbcType=BIGINT}



        and lending_amount  >=  #{lowerLendingAmount,jdbcType=INTEGER}


        and lending_amount  <=  #{upperLendingAmount,jdbcType=INTEGER}



        abd lending_period  >=  #{lowerLendingPeriod,jdbcType=INTEGER}


        and lending_period  <=  #{upperLendingPeriod,jdbcType=INTEGER}



        and expiration_date  >=  #{lowerExpirationDate,jdbcType=BIGINT}


        and expiration_date  <=  #{upperExpirationDate,jdbcType=BIGINT}
