mapper.select:
select claim_match.credit_contract_number as creditContractNumber,
investment_user.lending_contract_number as lendingContractNumber, investment_user.value_date_start as valueDateStart, investment_user.value_date_end as valueDateEnd,
investment_product.name as productName,
reception_users.id_name as userName
from claim_match left join investment_user on claim_match.claim_id = investment_user.claim_id left join investment_product on investment_user.product_id = investment_product.id left join reception_users on investment_user.uid = reception_users.id
claim_match.claim_id = #{claimID}
and claim_match.status = #{status}
and investment_product.name like "%"#{productName}"%"
and investment_user.lending_contract_number like "%"#{lendingContractNumber}"%"
and reception_users.id_name like "%"#{userName}"%"
and investment_user.value_date_start >= #{valueStartlowerDate}
and investment_user.value_date_start <= #{valueStartupperDate}
and investment_user.value_date_end >= #{valueEndlowerDate}
and investment_user.value_date_end <= #{valueEndupperDate}

mapper.insert:
insert into claim_match (claim_id, investment_user_id,
  credit_contract_number, status,gmt_create, gmt_update
  )
values (#{claimId,jdbcType=BIGINT}, #{investmentUserId,jdbcType=BIGINT},
  #{creditContractNumber,jdbcType=VARCHAR},#{status,jdbcType=TINYINT}, #{gmtCreate,jdbcType=BIGINT}, #{gmtUpdate,jdbcType=BIGINT}
  )
