mapper.select:
select investment_user.id as id, investment_user.value_date_start as valueDateStart, investment_user.value_date_end as valueDateEnd, investment_user.investment_amount as investmentAmount, investment_user.lending_contract_number as lendingContractNumber, investment_user.distributed_income as distributedIncome, investment_user.expected_income - investment_user.distributed_income as undistributedIncome, investment_user.investment_status as investmentStatus,
reception_users.id_name as userName, reception_users.phone as phone,
investment_product.name as productName,
claim_match.credit_contract_number as creditContractNumber from investment_user left join reception_users on investment_user.uid = reception_users.id left join investment_product ON investment_user.product_id = investment_product.id left join claim_match on investment_user.id = claim_match.investment_user_id
investment_user.uid = #{uid,jdbcType=BIGINT}
and investment_product.name like "%"#{productName,jdbcType=VARCHAR}"%"
and investment_user.lending_contract_number like "%"#{lendingContractNumber,jdbcType=VARCHAR}"%"
and claim_match.credit_contract_number like "%"#{creditContractNumber,jdbcType=VARCHAR}"%"
and investment_user.investment_status = #{investmentStatus,jdbcType=TINYINT}
and investment_user.investment_status != 0
and investment_user.value_date_start >= #{valueStartlowerDate,jdbcType=BIGINT}
and investment_user.value_date_start <= #{valueStartupperDate,jdbcType=BIGINT}
and investment_user.value_date_end >= #{valueEndlowerDate,jdbcType=BIGINT}
and investment_user.value_date_end <= #{valueEndupperDate,jdbcType=BIGINT}

select uid
from investment_user
where claim_id = #{claimID}

mapper.update:
update investment_user
set
  investment_status = #{investmentStatus,jdbcType=TINYINT},
  gmt_update = #{gmtUpdate,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

update investment_user
set
    lending_contract_number = #{lendingContractNumber,jdbcType=VARCHAR},
    gmt_update = #{gmtUpdate,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}
