mapper.select:
select

from message_platform


       title = #{title,jdbcType=VARCHAR}


     and  sending_crowd = #{sendingCrowd,jdbcType=TINYINT}


      and status = #{status,jdbcType=TINYINT}


     and  editors = #{editors,jdbcType=VARCHAR}


     and  gmt_update   >=   #{lowerDate,jdbcType=BIGINT}


     and  gmt_update   <=   #{upperDate,jdbcType=BIGINT}
