mapper.select:
select proposal.id as id, proposal.proposal as proposal, proposal.gmt_create as gmtCreate, reception_users.id_name as name, reception_users.phone as phone, reception_users.email as email from proposal left join reception_users on proposal.uid=reception_users.id
and reception_users.id_name like "%"#{name,jdbcType=VARCHAR}"%"
and reception_users.phone like "%"#{phone,jdbcType=BIGINT}"%"
and reception_users.email like "%"#{email,jdbcType=VARCHAR}"%"
and proposal.gmt_create >= #{lowerGmtCreate,jdbcType=BIGINT}
and proposal.gmt_create <= #{upperGmtCreate,jdbcType=BIGINT}

mapper.update:
update proposal
set uid = #{uid,jdbcType=BIGINT},
  proposal = #{proposal,jdbcType=VARCHAR},
  gmt_create = #{gmtCreate,jdbcType=BIGINT},
  gmt_update = #{gmtUpdate,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}
