mapper.select:
select

from reception_users
order by gmt_create desc;

select

from reception_users


        id_name like "%"#{idName,jdbcType=VARCHAR}"%"


        and phone like "%"#{phone,jdbcType=VARCHAR}"%"


        and manager_number like "%" #{managerNumber,jdbcType=VARCHAR}"%"


        and status =  #{status,jdbcType=TINYINT}


       and gmt_create  >=   #{lowerDate,jdbcType=BIGINT}


        and gmt_create  <=   #{upperDate,jdbcType=BIGINT}
